import { createStore, action, computed, thunk } from "easy-peasy";
import { setScanUrl, setScanCtgs, setScanInProgress, setMetadata, reSetScanUrl, addInfoEvent, addErrorEvent, addDataEvent, startStream, setDb, initDb } from "../eventHandlers/eventHandlers.js";
var Store = createStore({
  scanUrl: "",
  scanCtgs: "",
  scanInProgress: false,
  metadata: {},
  infoEvents: [],
  dataEvents: [],
  errorEvents: [],
  db: {},
  setScanUrl: action(function (state, scanUrl) {
    return setScanUrl(state, scanUrl);
  }),
  setScanCtgs: action(function (state, scanCtgs) {
    return setScanCtgs(state, scanCtgs);
  }),
  setScanInProgress: action(function (state, check) {
    return setScanInProgress(state, check);
  }),
  setMetadata: action(function (state, meta) {
    return setMetadata(state, meta);
  }),
  reSetScanUrl: action(function (state) {
    return reSetScanUrl(state);
  }),
  addInfoEvent: action(function (state, event) {
    addInfoEvent(state, event);
  }),
  addDataEvent: action(function (state, payload) {
    addDataEvent(state, payload);
  }),
  addErrorEvent: action(function (state, event) {
    addErrorEvent(state, event);
  }),
  startStream: thunk(function (actions, query) {
    return startStream(actions, query);
  }),
  setDb: action(function (state, db) {
    return setDb(state, db);
  }),
  initDb: thunk(function (actions) {
    return initDb(actions);
  })
});
export default Store;
