exports.formatTs = function (timestamp) {
  var addZero = function addZero(d) {
    return d = d > 9 ? d : "0" + d;
  };

  var month = addZero(new Date(timestamp).getMonth() + 1);
  var day = addZero(new Date(timestamp).getDate());
  var hour = addZero(new Date(timestamp).getHours());
  var minute = addZero(new Date(timestamp).getMinutes());
  var second = addZero(new Date(timestamp).getSeconds());
  return "".concat(day, "-").concat(month, " ").concat(hour, "-").concat(minute, "-").concat(second);
};
